const std = @import("std");

// Using the @cImport and @cInclude built-ins we can import the header file
// autogenerated by the Go c-archive build process. This translates the header from
// C to Zig, to see what is generated by this process you can run `zig translate-c
// libhello.h` after the build process.
pub const go = @cImport({
    @cInclude("libhello.h");
});

const stdout = std.io.getStdOut().writer();

pub fn main() !void {
    try stdout.print("Hello Ziguana\n", .{});
    go.HelloGopher();
}
